CREATE DEFINER=`root`@`localhost` PROCEDURE `avgDiscount`(IN cat varchar(100), IN subcat varchar(100), IN date date)
BEGIN
    DECLARE avgp DECIMAL(5,2);
    DECLARE id INT;
    DECLARE sale_price DECIMAL(5,2);
    DECLARE sale_date DATE;
    DECLARE finished INTEGER DEFAULT 0;

    DECLARE productCur1 CURSOR FOR
        SELECT sales_history.product_id, price, sales_history.upl_date  FROM sales_history
            INNER JOIN product ON sales_history.product_id = product.product_id
            INNER JOIN category ON product.cat_id = category.cat_id
                WHERE  CAST(sales_history.upl_date AS DATE) <= CAST(date AS DATE) AND  CAST(sales_history.upl_date AS DATE) > CAST(date AS DATE) - INTERVAL 7 DAY
                    AND category.cat_id = cat;

    DECLARE productCur2 CURSOR FOR
        SELECT sales_history.product_id, price, sales_history.upl_date  FROM sales_history
                INNER JOIN product ON sales_history.product_id = product.product_id
                INNER JOIN subcategory ON product.cat_id = subcategory.cat_id
                    WHERE  CAST(sales_history.upl_date AS DATE) <= CAST(date AS DATE) AND  CAST(sales_history.upl_date AS DATE) > CAST(date AS DATE) - INTERVAL 7 DAY
                      AND subcategory.subcat_id=subcat;

    DECLARE CONTINUE HANDLER
    FOR NOT FOUND SET finished=1;

    DROP TEMPORARY TABLE IF EXISTS temp;
    CREATE TEMPORARY TABLE temp(
        product_id INT,
        price_diff DECIMAL(5,2),
        sale_date DATE
    );


    IF subcat IS NULL THEN

        OPEN productCur1;

        calculate1: LOOP
            FETCH productCur1 INTO id, sale_price, sale_date;
            IF finished = 1 THEN
                LEAVE calculate1;
            END IF;

           SELECT AVG(sales_history.price) INTO avgp FROM sales_history
                WHERE sales_history.product_id=id AND CAST(sales_history.upl_date AS DATE) < CAST(date AS DATE)
                  AND CAST(sales_history.upl_date AS DATE) >= (date - INTERVAL 7 DAY);


            INSERT INTO temp VALUE (id, avgp-sale_price, sale_date);

        END LOOP;
    ELSE

        OPEN productCur2;

        calculate2: LOOP
            FETCH productCur2 INTO id, sale_price, sale_date;
            IF finished = 1 THEN
                LEAVE calculate2;
            END IF;

            SELECT AVG(sales_history.price) INTO avgp FROM sales_history
                WHERE sales_history.product_id=id AND CAST(sales_history.upl_date AS DATE) < CAST(date AS DATE)
                  AND CAST(sales_history.upl_date AS DATE) >= (date - INTERVAL 7 DAY);

            INSERT INTO temp VALUE (id, avgp-sale_price, sale_date);

        END LOOP;
    END IF;

    select * from temp;
    
    SELECT AVG(price_diff) as avg FROM temp;
      

END ;;
