CREATE DEFINER=`root`@`localhost` PROCEDURE `getSaleData`(IN shopID smallint)
BEGIN
    DECLARE avgp1 DECIMAL(5,2);
    DECLARE avgp2 DECIMAL(5,2);
    DECLARE productID SMALLINT;
    DECLARE productPrice DECIMAL(5,2);
    DECLARE saleID SMALLINT;
    DECLARE prop1 ENUM('true','false');
    DECLARE prop2 ENUM('true','false');
    DECLARE finished INTEGER DEFAULT 0;
    DECLARE latestDate DATETIME;

    DECLARE curSale CURSOR FOR
        SELECT sale_id,product_id, price FROM sale
            WHERE shop_id = shopID
            ORDER BY sale_id ASC;
    DECLARE CONTINUE HANDLER
        FOR NOT FOUND SET finished = 1;

    DROP TEMPORARY TABLE IF EXISTS temp;
    CREATE TEMPORARY TABLE temp(
        sale_id SMALLINT,
        DailyLower ENUM('true','false'),
        WeeklyLower ENUM('true','false')
    );

    OPEN curSale;

    getSale: LOOP
        FETCH curSale INTO saleID,productID,productPrice;
        IF finished = 1 THEN
                        LEAVE getSale;
                END IF;

        SELECT MAX(upl_date) INTO latestDate FROM sale WHERE product_id=productID;

        CALL avgPrice(productID, latestDate, avgp1, avgp2);
        CALL priceIsLower(avgp1, productPrice, prop1);
        CALL priceIsLower(avgp2, productPrice, prop2);

        IF (avgp1 IS NULL AND avgp2 IS NULL) THEN
            SET prop1 = 'true';
            SET prop2 = 'true';
        END IF;

        INSERT INTO temp VALUES(saleID,prop1,prop2);

    END LOOP;


    SELECT sale.sale_id,product.name,product.image,user.username,user.total_score,sale.shop_id,sale.price,sale.upl_date,sale.exp_date,sale.likes,sale.dislikes,sale.in_stock, temp.DailyLower as prop1, temp.WeeklyLower as prop2 FROM sale
    INNER JOIN product ON sale.product_id = product.product_id
    INNER JOIN user ON sale.user_id = user.user_id
    INNER JOIN temp ON sale.sale_id=temp.sale_id
        ORDER BY sale.sale_id ASC;

END ;;
