CREATE DEFINER=`root`@`localhost` PROCEDURE `ratingChange`(IN userId SMALLINT, IN saleId SMALLINT, IN rateType ENUM('like', 'dislike'))
BEGIN
DECLARE rType ENUM('like', 'dislike');
DECLARE count INT;
SELECT COUNT(*), type INTO count, rType FROM rating_history
WHERE user_id=userId AND sale_id=saleId;
IF (count > 0) THEN

  IF (rType = 'like') THEN
      UPDATE rating_history
      SET type = 'dislike' WHERE user_id=userId AND sale_id=saleId;
  ELSE 
      UPDATE rating_history
      SET type = 'like' WHERE user_id=userId AND sale_id=saleId;
  END IF;

ELSE

  IF (rateType = 'like') THEN
      INSERT INTO rating_history VALUES(null,userId,saleId,"like",now());
  ELSE 
      INSERT INTO rating_history VALUES (null,userId,saleId,"dislike",now());
  END IF;

END IF;
END ;;
